@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+JP:wght@400;500;700;900&family=Zen+Kaku+Gothic+New:wght@700;900&display=swap');

:root {
    --primary-bg: #1a2a3a;
    --accent-gold: #c5a059;
    --text-white: #ffffff;
    --text-sub: #b0c4de;
}

section.premium-slide {
    width: 100%;
    height: 100%;
    padding: 60px 80px 160px 80px;
    box-sizing: border-box;
    background: radial-gradient(circle at 30% 20%, #2c3e50 0%, #000000 100%);
    color: var(--text-white);
    font-family: 'Noto Sans JP', sans-serif;
    position: relative;
    overflow: hidden;
    display: flex;
    justify-content: center;
    align-items: center;
}

.intro-content {
    text-align: center;
    z-index: 2;
}

.sub-title {
    font-size: 42px;
    color: var(--text-sub);
    margin-bottom: 20px;
    letter-spacing: 0.1em;
}

.main-title {
    font-size: 110px;
    font-family: 'Zen Kaku Gothic New', sans-serif;
    font-weight: 900;
    line-height: 1.1;
    margin: 0;
    text-shadow: 0 10px 30px rgba(0, 0, 0, 0.5);
}

.main-title .highlight {
    color: var(--accent-gold);
}

.divider {
    width: 100px;
    height: 6px;
    background: var(--accent-gold);
    margin: 50px auto;
}

.intro-footer {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 20px;
}

.highlight-box {
    background: var(--accent-gold);
    color: #000;
    padding: 10px 30px;
    font-weight: 700;
    border-radius: 4px;
    font-size: 24px;
}

.intro-footer p {
    font-size: 32px;
    margin: 0;
}

