@import url('https://fonts.googleapis.com/css2?family=Material+Symbols+Outlined:opsz,wght,FILL,GRAD@24,400,0,0');
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+JP:wght@400;700&display=swap');

:root {
    --bg-dark: #121212;
    --card-bg: #1e1e1e;
    --accent-gold: #c5a059;
    --text-main: #ffffff;
}

section.premium-slide {
    width: 100%;
    height: 100%;
    padding: 60px 60px 160px 60px;
    box-sizing: border-box;
    background: var(--bg-dark);
    color: var(--text-main);
    font-family: 'Noto Sans JP', sans-serif;
    display: flex;
    flex-direction: column;
}

.slide-header {
    text-align: center;
    margin-bottom: 40px;
}

.slide-header h1 {
    font-size: 64px;
    margin: 0;
}

.gold {
    color: var(--accent-gold);
}

.three-columns {
    display: flex;
    justify-content: center;
    gap: 30px;
    flex: 1;
    align-items: stretch;
}

.column-card {
    background: var(--card-bg);
    border: 1px solid #333;
    border-radius: 12px;
    padding: 30px;
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    opacity: 0;
    /* Animated */
    transition: transform 0.3s;
}

.column-card.visible {
    opacity: 1;
}

.card-icon .material-symbols-outlined {
    font-size: 80px;
    color: var(--accent-gold);
    margin-bottom: 20px;
}

.column-card h3 {
    font-size: 36px;
    margin: 10px 0;
}

.badge {
    background: #333;
    color: #fff;
    padding: 5px 15px;
    border-radius: 20px;
    font-weight: bold;
    margin-bottom: 20px;
    border: 1px solid var(--accent-gold);
}

.column-card ul {
    list-style: none;
    padding: 0;
    margin: 0;
    width: 100%;
    text-align: left;
}

.column-card li {
    font-size: 24px;
    padding: 10px 0;
    border-bottom: 1px solid #444;
}

.column-card li:last-child {
    border-bottom: none;
}

/* Font Fix for Icons */
.material-symbols-outlined {
    font-family: 'Material Symbols Outlined';
    font-weight: normal;
    font-style: normal;
    line-height: 1;
    display: inline-block;
}

@import url('https://fonts.googleapis.com/css2?family=Material+Symbols+Outlined:opsz,wght,FILL,GRAD@24,400,0,0');
