@import url('https://fonts.googleapis.com/css2?family=Material+Symbols+Outlined:opsz,wght,FILL,GRAD@24,400,0,0');
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+JP:wght@400;700;900&display=swap');

section.premium-slide {
    width: 100%;
    height: 100%;
    padding: 60px 100px 150px 100px;
    box-sizing: border-box;
    background: #191919;
    color: #fff;
    font-family: 'Noto Sans JP', sans-serif;
    display: flex;
    flex-direction: column;
}

.slide-header {
    text-align: center;
    margin-bottom: 30px;
}

.slide-header h1 {
    font-size: 56px;
    margin: 0;
}

.slide-header small {
    display: block;
    font-size: 32px;
    color: #aaa;
    margin-top: 10px;
    font-weight: normal;
}

.calc-container {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 15px;
    flex: 1;
    justify-content: center;
}

.calc-row {
    background: #2a2a2a;
    width: 80%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 20px 40px;
    border-radius: 10px;
    border-left: 10px solid #c5a059;
    opacity: 0;
    /* Ani */
}

.calc-label {
    font-size: 36px;
    font-weight: bold;
    display: flex;
    align-items: center;
    gap: 20px;
}

.calc-label .icon {
    font-size: 48px;
    color: #c5a059;
}

.calc-value {
    font-size: 56px;
    font-weight: 900;
    font-family: monospace;
    /* For alignment */
}

.calc-value span {
    font-size: 28px;
    font-weight: normal;
    margin-left: 10px;
}

.calc-op {
    font-size: 40px;
    color: #555;
    line-height: 0.5;
}

.divider-line {
    width: 80%;
    height: 4px;
    background: #fff;
    margin: 10px 0;
}

.calc-total {
    text-align: center;
    opacity: 0;
    /* Ani */
}

.total-label {
    font-size: 32px;
    color: #aaa;
    margin-bottom: 5px;
}

.total-value {
    font-size: 100px;
    font-weight: 900;
    color: #ff5252;
    line-height: 1;
    text-shadow: 0 0 20px rgba(255, 82, 82, 0.4);
}

.total-value .approx {
    font-size: 48px;
    margin-right: 10px;
}

.total-value .unit {
    font-size: 48px;
    margin-left: 10px;
    color: #fff;
}

.sub-total {
    font-size: 24px;
    color: #777;
    margin-top: 10px;
}

.material-symbols-outlined {
    font-family: 'Material Symbols Outlined';
    font-weight: normal;
    display: inline-block;
}

@import url('https://fonts.googleapis.com/css2?family=Material+Symbols+Outlined:opsz,wght,FILL,GRAD@24,400,0,0');
