section.premium-slide {
    width: 100%;
    height: 100%;
    padding: 60px 60px 160px 60px;
    box-sizing: border-box;
    background: #0f172a;
    color: #fff;
    font-family: 'Noto Sans JP', sans-serif;
    display: flex;
    flex-direction: column;
}

.slide-header {
    text-align: center;
    margin-bottom: 60px;
}

.slide-header h1 {
    font-size: 64px;
}

.gold {
    color: #fbbf24;
}

.timeline-container {
    display: flex;
    justify-content: center;
    align-items: flex-start;
    gap: 20px;
}

.time-step {
    background: #1e293b;
    width: 300px;
    padding: 30px 20px;
    border-radius: 20px;
    position: relative;
    text-align: center;
    border: 2px solid transparent;
    opacity: 0;
    /* Ani */
    transition: all 0.5s;
}

.active.time-step {
    background: #1e293b;
    border-color: #fbbf24;
    transform: scale(1.05);
    box-shadow: 0 0 30px rgba(251, 191, 36, 0.2);
}

.step-circle {
    width: 60px;
    height: 60px;
    background: linear-gradient(135deg, #3b82f6, #1d4ed8);
    border-radius: 50%;
    font-size: 32px;
    font-weight: 900;
    line-height: 60px;
    margin: -60px auto 20px auto;
    /* Pop out top */
    color: #fff;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.5);
}

.active .step-circle {
    background: linear-gradient(135deg, #fbbf24, #d97706);
}

.step-content h3 {
    font-size: 36px;
    margin: 10px 0;
    color: #93c5fd;
}

.active .step-content h3 {
    color: #fbbf24;
}

.step-content p {
    font-size: 22px;
    line-height: 1.5;
    color: #cbd5e1;
}

.caution {
    background: #ef4444;
    color: #fff;
    font-weight: bold;
    padding: 5px 10px;
    border-radius: 5px;
    display: inline-block;
    margin-top: 15px;
    font-size: 18px;
}

.arrow {
    width: 40px;
    height: 100px;
    /* actually just centering */
    border-top: 4px dashed #475569;
    align-self: flex-start;
    margin-top: 60px;
    /* Align with cards */
    position: relative;
}

.arrow::after {
    content: '▶';
    position: absolute;
    right: -10px;
    top: -16px;
    color: #475569;
    font-size: 24px;
}

