customCss:
/* Custom Theme: Pachinko Gold & Navy (Grid Layout) */
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+JP:wght@500;700;900&family=Material+Symbols+Outlined:opsz,wght,FILL,GRAD@24,400,0,0&display=swap');

:root {
    --primary-color: #ffd700;
    /* Gold */
    --accent-color: #ffffff;
    --bg-gradient: linear-gradient(135deg, #001f3f 0%, #003366 100%);
    /* Navy */
    --text-main: #ffffff;
    --text-sub: #b0bec5;
    --card-bg: rgba(255, 255, 255, 0.1);
    --shadow: 0 8px 32px rgba(0, 0, 0, 0.4);
}

body {
    margin: 0;
    padding: 0;
}

section {
    display: flex;
    flex-direction: column;
    height: 100%;
    padding: 60px 40px 180px 40px !important;
    /* side margins reduced for grid */
    box-sizing: border-box !important;
    background: var(--bg-gradient);
    font-family: 'Noto Sans JP', sans-serif;
    color: var(--text-main);
    position: relative;
    overflow: hidden;
}

.slide-header {
    margin-bottom: 40px;
    padding-bottom: 20px;
    border-bottom: 4px solid var(--primary-color);
    display: flex;
    justify-content: space-between;
    align-items: flex-end;
    margin: 0 40px 40px 40px;
}

.slide-header h1 {
    font-size: 72px;
    margin: 0;
    color: var(--primary-color);
    font-weight: 900;
    line-height: 1.1;
    text-shadow: 0 0 20px rgba(255, 215, 0, 0.3);
}

.slide-number {
    font-size: 80px;
    font-weight: 900;
    color: rgba(255, 215, 0, 0.1);
    position: absolute;
    top: 20px;
    right: 40px;
    line-height: 1;
}

.animate-item {
    opacity: 0;
    transform: scale(0.9) translateY(20px);
    transition: all 0.6s cubic-bezier(0.16, 1, 0.3, 1);
}

.grid-container {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 20px;
    flex: 1;
    align-items: stretch;
    padding: 0 20px;
    justify-content: center;
}

.grid-card {
    background: var(--card-bg);
    padding: 40px 20px;
    border-radius: 20px;
    box-shadow: var(--shadow);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 215, 0, 0.2);
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    position: relative;
}

.card-badge {
    position: absolute;
    top: -20px;
    left: 20px;
    background: var(--primary-color);
    color: #001f3f;
    width: 60px;
    height: 60px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 32px;
    font-weight: 900;
    box-shadow: 0 4px 15px rgba(255, 215, 0, 0.4);
}

.card-icon {
    margin: 30px 0 20px 0;
}

.card-icon span {
    font-size: 100px !important;
    color: var(--primary-color);
    filter: drop-shadow(0 0 10px rgba(255, 215, 0, 0.3));
}

.grid-card h3 {
    margin: 10px 0;
    font-size: 38px;
    color: var(--primary-color);
    font-weight: 800;
    line-height: 1.2;
}

.grid-card p {
    margin: 10px 0 0 0;
    font-size: 24px;
    color: var(--text-sub);
    line-height: 1.4;
}

/* Specific highlight for second card to emphasize 'change' */
#item-2 {
    background: rgba(255, 215, 0, 0.15);
    border: 2px solid var(--primary-color);
}

/* Material Symbols Class */
.material-symbols-outlined {
    font-family: 'Material Symbols Outlined';
    font-weight: normal;
    font-style: normal;
    line-height: 1;
    letter-spacing: normal;
    text-transform: none;
    display: inline-block;
    white-space: nowrap;
    word-wrap: normal;
    direction: ltr;
    -webkit-font-feature-settings: 'liga';
    -webkit-font-smoothing: antialiased;
}
